    def __init__(self):
        self.main_conn = backend.ConnectionAPI()
    def logout(self):
        self.main_conn.user_table_instance.connection.close()
        self.__init__()
        self.stackedWidget.setCurrentWidget(self.Home)

    def fetch_records(self):

        self.data = self.main_conn.user_table_instance.extract_data(self.userx[0])
        
        self.MainTable.setRowCount(len(self.data))
        row = 0
        for data in self.data:
            
            self.MainTable.setItem(row,0,QtWidgets.QTableWidgetItem(data[1]))
            self.MainTable.setItem(row,1,QtWidgets.QTableWidgetItem(data[3]))
            self.MainTable.setItem(row,2,QtWidgets.QTableWidgetItem(str(data[4])))
            self.MainTable.setItem(row,3,QtWidgets.QTableWidgetItem(str(data[2])))
            self.MainTable.setItem(row,4,QtWidgets.QTableWidgetItem(data[5]))
            self.MainTable.setItem(row,5,QtWidgets.QTableWidgetItem(str(data[2]*float(data[4]))))

            row += 1
            




    def check_login(self):
        self.userx = []
        self.userx.append(self.user_le_l.text())
        self.userx.append(self.pass_le_l.text())
        
        if self.main_conn.check_user_credentials(self.userx[0],self.userx[1]):
            self.stackedWidget.setCurrentWidget(self.Main)
            self.wel_m.setText(f"Welcome {self.userx[0]} !")
            self.fetch_records()
        else:
            self.show_pop("Invalid credentials")

    def show_pop(self, msg1):
        
        self.msg = QMessageBox()
        self.msg.setWindowTitle("Attention")
        self.msg.setText(msg1)
        self.msg.setIcon(QMessageBox.Critical)
        self.msg.exec_() 

    def fetch_register(self):
        x = []
        x.append(self.user_le_r.text())
        x.append(self.pass_le_r.text())
        x.append(self.email_le_r.text())
        x.append(self.name_le_r.text())
        return x

    def do_register(self):
        l = self.fetch_register()
        t = self.main_conn.post_into_user_metadata(l[0],l[1],l[3],l[2])
        if t:
            self.show_pop("Successfully Registered!")
        else:
            self.show_pop("Error while registering!")
        self.stackedWidget.setCurrentWidget(self.Home)
